from odoo import api, models, fieldsclass StudentFeesUpdateWizard(models.TransientModel):    _name = "student.feees.update.wizard"    total_fees = fields.Float(string="Fees")    def update_student_fees(self):        # print("Yeah successfully click on update_student_fees method........")        self.env['school.student'].browse(self._context.get("active_ids")).update({'total_fees': self.total_fees})        return Trueclass StudentFeesUpdateExtendWizard(models.TransientModel):    _inherit = "student.feees.update.wizard"    parent_name = fields.Char(string="Parent Name")    def update_student_fees(self):        # print("This is inherited def update_student_fees(self): method......")        #return True        # Without Super statement        varaiable = super(StudentFeesUpdateExtendWizard, self).update_student_fees()        #With super statement        return varaiable